    /*Check for someone writing to pages. Yeah, right?*/
    if not exists ( select 1
      from #SkipChecks
      where   DatabaseName is null and CheckID = 209 )
      and @TraceFileIssue = 0
					begin

      if @Debug in (1, 2) raiserror('Running CheckId [%d].', 0, 1, 209) with NOWAIT

