        end;

        /* Look for scalar user-defined functions */

        if not exists ( select 1
        from #SkipChecks
        where   DatabaseName is null and CheckID = 67 )
							begin

          if @Debug in (1, 2) raiserror('Running CheckId [%d].', 0, 1, 67) with NOWAIT;

