		FROM #missing_index_pretty AS mip
		GROUP BY mip.QueryHash, mip.SqlHandle, mip.impact
						)
		UPDATE bbcp
			SET bbcp.missing_indexes = m.full_details
		FROM ##bou_BlitzCacheProcs AS bbcp
		JOIN missing AS m
		ON m.SqlHandle = bbcp.SqlHandle
		AND SPID = @@SPID
		OPTION (RECOMPILE);

	
	END;

